            return foundDigit;
        }
        if (!allowSigns
            && (chars[i] == 'd'
                || chars[i] == 'D'
                || chars[i] == 'f'
                || chars[i] == 'F')) {
            return foundDigit;
        }
        if (chars[i] == 'l'
            || chars[i] == 'L') {
            // Check if there's a digit after the decimal point if a decimal is present
            if (hasDecPoint) {
                boolean hasDigitAfterDecPoint = false;
                for (int j = i - 1; j >= start; j--) {
                    if (chars[j] >= '0' && chars[j] <= '9') {
                        hasDigitAfterDecPoint = true;
                        break;
                    }
                }
                if (!hasDigitAfterDecPoint) {
                    return false;
                }
            }
            return foundDigit && !hasExp;
        }
        // last character is illegal
        return false;
    }
    // allowSigns is true iff the val ends in 'E'
    // found digit it to make sure weird stuff like '.' and '1E-' doesn't pass
    return !allowSigns && foundDigit;
}
