            currentEvent.stepAccepted(eventT, eventY);
            isLastStep = currentEvent.stop();

            // handle the first part of the step, up to the event
            for (final StepHandler handler : stepHandlers) {
                handler.handleStep(newInterpolator, isLastStep);
            }

            if (isLastStep) {
                // the event asked to stop integration
                System.arraycopy(eventY, 0, y, 0, y.length);
                for (final EventState remaining : occuringEvents) {
                    remaining.stepAccepted(eventT, eventY);
                }
                // Update the original interpolator with the new state
                interpolator.setSoftPreviousTime(newInterpolator.getSoftPreviousTime());
                interpolator.setSoftCurrentTime(newInterpolator.getSoftCurrentTime());
                interpolator.setInterpolatedTime(newInterpolator.getInterpolatedTime());
                interpolator.setInterpolatedState(newInterpolator.getInterpolatedState());
                interpolator.setInterpolatedDerivatives(newInterpolator.getInterpolatedDerivatives());
                return eventT;
            }

            boolean needReset = currentEvent.reset(eventT, eventY);
            if (needReset) {
                // some event handler has triggered changes that
                // invalidate the derivatives, we need to recompute them
                System.arraycopy(eventY, 0, y, 0, y.length);
                computeDerivatives(eventT, y, yDot);
                resetOccurred = true;
                for (final EventState remaining : occuringEvents) {
                    remaining.stepAccepted(eventT, eventY);
                }
                // Update the original interpolator with the new state
                interpolator.setSoftPreviousTime(newInterpolator.getSoftPreviousTime());
                interpolator.setSoftCurrentTime(newInterpolator.getSoftCurrentTime());
                interpolator.setInterpolatedTime(newInterpolator.getInterpolatedTime());
                interpolator.setInterpolatedState(newInterpolator.getInterpolatedState());
                interpolator.setInterpolatedDerivatives(newInterpolator.getInterpolatedDerivatives());
                return eventT;
            }

            // prepare handling of the remaining part of the step
            previousT = eventT;
            newInterpolator.setSoftPreviousTime(eventT);
            newInterpolator.setSoftCurrentTime(currentT);

            // check if the same event occurs again in the remaining part of the step
            if (currentEvent.evaluateStep(newInterpolator)) {
                // the event occurs during the current step
                occuringEvents.add(currentEvent);
            }

        }

        // last part of the step, after the last event
        newInterpolator.setInterpolatedTime(currentT);
        final double[] currentY = newInterpolator.getInterpolatedState();
        for (final EventState state : eventsStates) {
            state.stepAccepted(currentT, currentY);
            isLastStep = isLastStep || state.stop();
        }
        isLastStep = isLastStep || Precision.equals(currentT, tEnd, 1);

        // handle the remaining part of the step, after all events if any
        for (StepHandler handler : stepHandlers) {
            handler.handleStep(newInterpolator, isLastStep);
        }

        // Update the original interpolator with the final state
        interpolator.setSoftPreviousTime(newInterpolator.getSoftPreviousTime());
        interpolator.setSoftCurrentTime(newInterpolator.getSoftCurrentTime());
        interpolator.setInterpolatedTime(newInterpolator.getInterpolatedTime());
        interpolator.setInterpolatedState(newInterpolator.getInterpolatedState());
        interpolator.setInterpolatedDerivatives(newInterpolator.getInterpolatedDerivatives());

        return currentT;
}
